package app.caihan.myscframe.ui.home;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import java.util.List;import app.caihan.myscframe.R;/** *  * * @author caihan * @date 2018/11/22 * @e-mail 93234929@qq.com * 维护者  */public class HomeAdapter extends BaseQuickAdapter<HomeItem,BaseViewHolder> {    public HomeAdapter() {        super(R.layout.home_item_view);    }    @Override    public void setNewData(List<HomeItem> data) {        super.setNewData(data);    }    @Override    protected void convert(BaseViewHolder helper, HomeItem item) {        helper.setText(R.id.text, item.getTitle());    }}