package com.caihan.myscframe.base;import android.support.annotation.IdRes;import android.view.View;import com.caihan.myscframe.MyApp;import com.caihan.scframe.framework.v1.support.MvpPresenter;import com.caihan.scframe.framework.v1.support.MvpView;import com.caihan.scframe.framework.v1.support.mvp.fragment.ScMvpFragment;import com.squareup.leakcanary.RefWatcher;import butterknife.ButterKnife;import butterknife.Unbinder;/** * 基类 * * @author caihan * @date 2018/2/18 * @e-mail 93234929@qq.com * 维护者 */public abstract class BaseScMvpFragment        <V extends MvpView, P extends MvpPresenter<V>>        extends ScMvpFragment<V, P> {    Unbinder mUnbinder;    @Override    protected void butterKnifeBind(View rootView) {        mUnbinder = ButterKnife.bind(this, rootView);    }    @Override    protected void butterKnifeUnBind() {        super.butterKnifeUnBind();    }    @Override    public void setImmersion() {        super.setImmersion();    }    @Override    public void onDestroyView() {        super.onDestroyView();        mUnbinder.unbind();    }    protected <T extends View> T findViewById(@IdRes int id){        if (mRootView != null){            return mRootView.findViewById(id);        }else {            throw new NullPointerException("mRootView为空");        }    }    @Override    public void onDestroy() {        super.onDestroy();        RefWatcher refWatcher = MyApp.getRefWatcher(getActivity());        refWatcher.watch(this);    }}