package com.caihan.myscframe.ui.home;import com.caihan.myscframe.R;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import java.util.List;/** * Created by caihan on 2017/1/23. */public class HomeAdapter extends BaseQuickAdapter<HomeItem,BaseViewHolder> {    public HomeAdapter() {        super(R.layout.home_item_view);    }    @Override    public void setNewData(List<HomeItem> data) {        super.setNewData(data);    }    @Override    protected void convert(BaseViewHolder helper, HomeItem item) {        helper.setText(R.id.text, item.getTitle());    }}